def find_destination_floor(line: str):
    return sum(1 if c == '(' else -1 for c in line)


def find_basement_position(line: str):
    return next(i for i, floor in enumerate(accumulate(1 if c == '(' else -1 for c in line)) if floor < 0) + 1


def part_1():
    return find_destination_floor(input)


def part_2():
    return find_basement_position(input)


